import React from "react";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";

import axios from "axios";

import { API_URL } from "../constants";

class NewListForm extends React.Component {
  state = {
    id: 0,
    user: "",
    item: "",
    duedate: "",
    priority: "",
    completed: "",

  };

  componentDidMount() {
    if (this.props.list) {
      const { id, user, item, duedate, priority, completed } = this.props.list;
      this.setState({ id, user, item, duedate, priority, completed });
    }
  }

  onChange = e => {
    this.setState({ [e.target.item]: e.target.value });
  };

  createList = e => {
    e.preventDefault();
    axios.post(API_URL, this.state).then(() => {
      this.props.resetState();
      this.props.toggle();
    });
  };

  editList = e => {
    e.preventDefault();
    axios.put(API_URL + this.state.id, this.state).then(() => {
      this.props.resetState();
      this.props.toggle();
    });
  };

  defaultIfEmpty = value => {
    return value === "" ? "" : value;
  };

  render() {
    return (
      <Form onSubmit={this.props.list ? this.editList : this.createList}>
        <FormGroup>
          <Label for="item">Task:</Label>
          <Input
            type="text"
            name="item"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.item)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="duedate">Due Date:</Label>
          <Input
            type="text"
            name="duedate"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.duedate)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="priority">Priority:</Label>
          <Input
            type="text"
            name="priority"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.priority)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="completed">Completed:</Label>
          <Input
            type="text"
            name="completed"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.completed)}
          />
        </FormGroup>
        <Button>Send</Button>
      </Form>
    );
  }
}

export default NewListForm;
